
export const generateAttestation = (entity, paymentMethod) => {
    // Configuration
    const SIGNATAIRE_NOM = "Jean-Philippe DUHERON";
    const SIGNATAIRE_QUALITE = "Vice-président escalade";
    const CLUB_NOM = "CAF La Roche Bonneville";
    const CLUB_ADRESSE_LIGNE1 = "36 rue Plain Château";
    const CLUB_ADRESSE_LIGNE2 = "74800 LA ROCHE SUR FORON";
    const CLUB_LEGAL = "Association loi 1901 / Siret : 447 842 329 000 16";
    const CLUB_IBAN = "Banque Populaire des Alpes / IBAN FR76 1680 7000 4084 0639 0119 404 / BIC CCBPFRPPGRE";

    const today = new Date().toLocaleDateString('fr-FR', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
    });

    const recetteAmount = entity.Recette ? parseFloat(entity.Recette) : 0;
    const recette = recetteAmount.toLocaleString('fr-FR', { minimumFractionDigits: 2 });
    const type = entity.Type || "Non spécifié";
    const nomEntite = entity.title || "Nom de l'entité inconnu";
    const adresseEntite = entity.address || "";

    // Logic for Description based on Type and Amount
    let description = type;

    if (type === 'Encart Pub') {
        if (recetteAmount === 85) description = "Encart publicitaire 1/8 page";
        else if (recetteAmount === 150) description = "Encart publicitaire 1/4 page";
        else if (recetteAmount === 260) description = "Encart publicitaire 1/2 page";
        else if (recetteAmount === 450) description = "Encart publicitaire Pleine page (intérieur)";
        else if (recetteAmount === 600) description = "Encart publicitaire 3e de couverture";
        else if (recetteAmount === 700) description = "Encart publicitaire 2e de couverture";
        else if (recetteAmount === 1500) description = "Pack Premium";
        else description = `Encart publicitaire (Montant personnalisé)`;
    } else if (type === 'Mécénat') {
        description = "Don (Mécénat)";
    }

    // Use absolute paths for images in public folder
    const logoUrl = 'data:image/png;base64,';
    const signatureUrl = 'data:image/png;base64,';
    const tamponUrl = 'data:image/jpeg;base64,';

    const content = `
        <!DOCTYPE html>
        <html lang="fr">
        <head>
            <meta charset="UTF-8">
            <title>Attestation de Paiement - ${nomEntite}</title>
            <style>
                * {
                    margin: 0;
                    padding: 0;
                    box-sizing: border-box;
                }
                html, body {
                    height: 100%;
                }
                body {
                    font-family: 'Arial', sans-serif;
                    line-height: 1.3;
                    color: #000;
                    display: flex;
                    flex-direction: column;
                    min-height: 100vh;
                    padding: 20px 25px;
                    max-width: 800px;
                    margin: 0 auto;
                }
                .content-wrapper {
                    flex: 1;
                    display: flex;
                    flex-direction: column;
                }
                .header-container {
                    display: flex;
                    justify-content: space-between;
                    align-items: flex-start;
                    margin-bottom: 10px;
                    border-bottom: 1px solid #ddd;
                    padding-bottom: 5px;
                }
                .sender-info {
                    font-size: 0.85rem;
                    line-height: 1.3;
                }
                .logo img {
                    max-width: 200px;
                    height: auto;
                }
                .title {
                    text-align: center;
                    margin: 12px 0;
                }
                .title h1 {
                    font-size: 20px;
                    text-transform: uppercase;
                    border-bottom: 2px solid #000;
                    display: inline-block;
                    padding-bottom: 5px;
                }
                .recipient-info {
                    margin: 12px 0;
                    text-align: right;
                    font-size: 1rem;
                }
                .main-content {
                    margin: 12px 0;
                    text-align: justify;
                    font-size: 1rem;
                }
                .main-content p {
                    margin: 6px 0;
                }
                .amount-box {
                    font-size: 1.5em;
                    text-align: center;
                    margin: 12px 0;
                    font-weight: bold;
                    background-color: #f9f9f9;
                    padding: 8px;
                    border: 1px dashed #ccc;
                }
                .signature-section {
                    margin-top: 30px;
                    padding-top: 15px;
                }
                .signature-date {
                    text-align: right;
                    margin-bottom: 8px;
                    font-size: 1rem;
                }
                .signature-name {
                    text-align: right;
                    margin-bottom: 10px;
                    font-size: 1rem;
                }
                .signature-images {
                    text-align: right;
                    margin-bottom: 0;
                }
                .signature-images img {
                    display: inline-block;
                    vertical-align: bottom;
                }
                .signature-images img.tampon {
                    width: 110px;
                    opacity: 0.9;
                    transform: rotate(-10deg);
                    margin-right: -20px;
                }
                .signature-images img.sig {
                    width: 150px;
                    margin-left: -20px;
                }
                .footer {
                    font-size: 0.75rem;
                    text-align: center;
                    color: #666;
                    border-top: 1px solid #ccc;
                    padding-top: 8px;
                    margin-top: auto;
                    padding-bottom: 10px;
                }
                @media print {
                    @page {
                        size: A4 portrait;
                        margin: 15mm;
                    }
                    body {
                        padding: 15px 20px;
                        min-height: 100vh;
                    }
                }
            </style>
        </head>
        <body>
            <div class="content-wrapper">
                <div class="header-container">
                    <div class="sender-info">
                        <strong>${CLUB_NOM}</strong><br>
                        ${CLUB_ADRESSE_LIGNE1}<br>
                        ${CLUB_ADRESSE_LIGNE2}<br>
                        <br>
                        ${CLUB_LEGAL}
                    </div>
                    <div class="logo">
                        <img src="${logoUrl}" alt="Logo CAF">
                    </div>
                </div>

                <div class="title">
                    <h1>Attestation de Paiement</h1>
                </div>

                <div class="recipient-info">
                    <strong>À l'attention de :</strong><br>
                    ${nomEntite}<br>
                    ${adresseEntite}
                </div>

                <div class="main-content">
                    <p>Je soussigné, <strong>${SIGNATAIRE_NOM}</strong>, agissant en qualité de <strong>${SIGNATAIRE_QUALITE}</strong> au sein du club <strong>${CLUB_NOM}</strong>,</p>
                    
                    <p>Confirme avoir reçu du partenaire <strong>${nomEntite}</strong> la somme de :</p>
                    
                    <div class="amount-box">
                        ${recette} €
                    </div>
                    
                    <p>Règlement effectué par <strong>${paymentMethod}</strong> pour : <strong>${description}</strong>.</p>
                    
                    <p>Cette attestation est délivrée pour servir et valoir ce que de droit.</p>
                </div>

                <div class="signature-section">
                    <div class="signature-date">
                        Fait à La Roche-sur-Foron, le ${today}
                    </div>
                    <div class="signature-name">
                        <strong>${SIGNATAIRE_NOM}</strong><br>
                        ${SIGNATAIRE_QUALITE}
                    </div>
                    <div class="signature-images">
                        <img src="${signatureUrl}" class="sig" alt="Signature">
                        <img src="${tamponUrl}" class="tampon" alt="Tampon">
                    </div>
                </div>
            </div>

            <div class="footer">
                ${CLUB_NOM}<br>
                ${CLUB_IBAN}
            </div>

            <script>
                window.onload = function() {
                    setTimeout(function() {
                        window.print();
                    }, 500);
                }
            </script>
        </body>
        </html>
    `;

    const printWindow = window.open('', '_blank', 'width=900,height=800');
    if (printWindow) {
        printWindow.document.write(content);
        printWindow.document.close();
    } else {
        alert("Veuillez autoriser les pop-ups pour générer l'attestation.");
    }
};
